from class_module a

limit #{firstIndex},#{pageSize}

select distinct m.*
from class_type as t,
class_type_module_relation as r,class_module as m
where
t.id=r.class_type_id and r.module_id=m.id and t.id=#{classId} and t.create_school_id=#{schoolId}

SELECT
count(*)
FROM
class_module a,
sys_config_item b,
sys_config_item c
where 1=1
and teach_method = #{teachMethod}
and id = #{id}
and publish_status = #{publishStatus}
and name like '%${name}%'
 and item_one_id = #{itemOneId}
and item_second_id = #{itemSecondId}
and module_type = #{moduleType}
and a.item_one_id = b.id
and c.id = a.item_second_id

SELECT
COUNT(0)
FROM class_type_module_relation WHERE module_id = #{id}

choose.when:
SELECT result.*, sct.`name` AS teacherName FROM ( SELECT l.id, l.lesson_name, l.lesson_date, l.lesson_date end_date, l.lesson_time_start, l.lesson_time_end, l.teacher_url_gh, l.replay_url_gh, l.liveroom_id_gh, l.teachers, l.assistants, l.live_company_type, n.id module_no_id, n.name, n.module_id, '1' AS classType FROM class_module_lesson l inner join class_module_no n ON l.module_no_id = n.id inner join class_module m ON n.module_id = m.id
inner join sys_config_teacher t ON (t.id = l.teachers or t.id = l.assistants) and t.user_id = #{teacherId}
WHERE m.school_id = #{schoolId}
and (l.lesson_name like concat('%',#{name},'%') or n.name like concat('%',#{name},'%'))
and l.id = #{id}
and l.lesson_date = #{lessonDate}
and l.lesson_date = #{lessonDate} and #{currentTime} > l.lesson_time_start and l.lesson_time_end > #{currentTime}
and ((l.lesson_date = #{lessonDate} and l.lesson_time_start > #{currentTime ) or l.lesson_date > #{lessonDate})
and ((#{lessonDate} = l.lesson_date and #{currentTime} > l.lesson_time_end) or #{lessonDate} > l.lesson_date)
and #{currentTime} > l.lesson_time_start and l.lesson_time_end > #{currentTime}
and (l.lesson_time_start > #{currentTime}) or l.lesson_date > #{lessonDate})
and (#{currentTime} > l.lesson_time_end) or #{lessonDate} > l.lesson_date)
and l.lesson_date = #{lessonDate} and l.lesson_time_start > #{currentTime}
and l.lesson_date > DATE_FORMAT(NOW(), '%Y-%m-%d') and l.lesson_date <= #{lessonDate}
and l.del_flag = 0 and m.del_flag = 0 and m.teach_method = 'TEACH_METHOD_LIVE' and m.publish_status = 'MODULE_PUBLISHED' and m.del_flag = 0 and m.teach_method = 'TEACH_METHOD_LIVE' and m.publish_status = 'MODULE_PUBLISHED' and n.del_flag = 0 group by l.id order by l.lesson_date,str_to_date(l.lesson_time_start,'%H:%i') LIMIT #{firstIndex},#{pageSize} )AS result LEFT JOIN sys_config_teacher sct ON result.teachers = sct.id

choose.otherwise:
SELECT * from ( SELECT result.*, sct.`name` AS teacherName FROM ( SELECT l.id, l.lesson_name, l.lesson_date, l.lesson_date end_date, l.lesson_time_start, l.lesson_time_end, l.teacher_url_gh, l.replay_url_gh, l.liveroom_id_gh, l.teachers, l.assistants, l.live_company_type, n.id module_no_id, n.name, n.module_id, '1' AS classType FROM class_module_lesson l inner join class_module_no n ON l.module_no_id = n.id inner join class_module m ON n.module_id = m.id
inner join sys_config_teacher t ON (t.id = l.teachers or t.id = l.assistants) and t.user_id = #{teacherId}
WHERE m.school_id = #{schoolId}
and (l.lesson_name like concat('%',#{name},'%') or n.name like concat('%',#{name},'%'))
and l.id = #{id}
and l.lesson_date = #{lessonDate}
and l.lesson_date = #{lessonDate} and #{currentTime} > l.lesson_time_start and l.lesson_time_end > s#{currentTime}
and ((l.lesson_date = #{lessonDate} and l.lesson_time_start > #{currentTime}) or l.lesson_date > #{lessonDate})
and ((#{lessonDate} = l.lesson_date and #{currentTime} > l.lesson_time_end) or #{lessonDate} > l.lesson_date)
and #{currentTime} > l.lesson_time_start and l.lesson_time_end > #{currentTime}
and ((l.lesson_time_start > #{currentTime}) or l.lesson_date > #{lessonDate})
and ((#{currentTime} > l.lesson_time_end) or #{lessonDate} > l.lesson_date)
and l.lesson_date = #{lessonDate} and l.lesson_time_start > #{currentTime}
and l.lesson_date > DATE_FORMAT(NOW(), '%Y-%m-%d') and l.lesson_date <= #{lessonDate}
and l.del_flag = 0 and m.del_flag = 0 and m.teach_method = 'TEACH_METHOD_LIVE' and m.publish_status = 'MODULE_PUBLISHED' and m.del_flag = 0 and m.teach_method = 'TEACH_METHOD_LIVE' and m.publish_status = 'MODULE_PUBLISHED' and n.del_flag = 0 group by l.id )AS result LEFT JOIN sys_config_teacher sct ON result.teachers = sct.id UNION ALL SELECT loc.id, loc.open_course_name AS lesson_name, loc.start_open_data AS lesson_date, loc.end_open_data end_date, loc.start_time AS lesson_time_start, loc.end_time AS lesson_time_end, loc.teacher_url_gh, loc.replay_url_gh, loc.liveroom_id_gh, loc.teacher_id AS teachers, '' as assistants, loc.live_service_provider as live_company_type, '' as module_no_id, '' as name, '' as module_id, '2' as classType, (SELECT sct.name FROM sys_config_teacher sct where id = loc.teacher_id) as teacherName
FROM live_open_course AS loc
inner join sys_config_teacher t ON (t.id = loc.teacher_id or t.id = loc.assistant_id) and t.user_id = #{teacherId}
WHERE loc.school_id = #{schoolId}
and loc.open_course_name like concat('%',#{name})
and ( #{lessonDate} >= loc.start_open_data and loc.end_open_data >= #{lessonDate})
and STR_TO_DATE(CONCAT(date_format(#{lessonDate} , '%y-%m-%d'), '', #{currentTime}), '%y-%m-%d %H:%i') > STR_TO_DATE(CONCAT(date_format(loc.start_open_data , '%y-%m-%d'), '', loc.start_time), '%y-%m-%d %H:%i') and STR_TO_DATE(CONCAT(date_format(loc.end_open_data , '%y-%m-%d'), '', loc.end_time), '%y-%m-%d %H:%i') > STR_TO_DATE(CONCAT(date_format(#{lessonDate} , '%y-%m-%d'), '', #{currentTime}), '%y-%m-%d %H:%i')
and ((loc.start_open_data = #{lessonDate} and loc.start_time > #{currentTime}) or loc.start_open_data > #{lessonDate})
and ((#{lessonDate} = loc.end_open_data and #{currentTime} > loc.end_time) or #{lessonDate} > loc.end_open_data)
and #{currentTime} > loc.start_time and loc.end_time > #{currentTime}
and ((loc.start_time > #{currentTime}) or loc.start_open_data > #{lessonDate})
and ((#{currentTime} > loc.end_time) or #{lessonDate} > loc.end_open_data)
and loc.end_open_data = #{lessonDate} and loc.start_time > #{currentTime}
and loc.end_open_data > DATE_FORMAT(NOW(), '%Y-%m-%d') and loc.end_open_data <= #{lessonDate}
and loc.publish_status = 1 and loc.del_flag = 0 ) as moreResult order by lesson_date,str_to_date(lesson_time_start,'%H:%i') LIMIT #{firstIndex},#{pageSize}

mapper.select:
	SELECT a.*, b.item_name AS itemOneName, c.item_name AS itemSecondName, (
		SELECT COUNT(id)
		FROM class_module_no d
		WHERE d.module_id = a.id
	) AS moduleNoNum
FROM class_module a 
LEFT JOIN sys_config_item b ON a.item_one_id = b.id
LEFT JOIN sys_config_item c ON a.item_second_id = c.id
where a.company_id=#{companyId}
and
	a.school_id=#{schoolId}
and a.item_one_id = #{itemOneId}
and a.item_second_id = #{itemSecondId}
and a.teach_method = #{teachMethod}
order by a.update_time desc
limit #{firstIndex},#{pageSize}

	SELECT count(a.id)
FROM class_module a 
LEFT JOIN sys_config_item b ON a.item_one_id = b.id
LEFT JOIN sys_config_item c ON a.item_second_id = c.id
where a.company_id=#{companyId}
and
	a.school_id=#{schoolId}
and a.item_one_id = #{itemOneId}
and a.item_second_id = #{itemSecondId}
and a.teach_method = #{teachMethod}

choose.when:
SELECT count(id) FROM ( SELECT l.id FROM class_module_lesson l inner join class_module_no n ON l.module_no_id = n.id inner join class_module m ON n.module_id = m.id
inner join sys_config_teacher t ON (t.id = l.teachers or t.id = l.assistants) and t.user_id = #{teacherId}
WHERE m.school_id = #{schoolId}
and (l.lesson_name like concat('%',#{name},'%') or n.name like concat('%',#{name},'%'))
and l.id = #{id}
and l.lesson_date = #{lessonDate}
and l.lesson_date = #{lessonDate} and str_to_date(#{currentTime},'%H:%i') > str_to_date(l.lesson_time_start,'%H:%i') and str_to_date(l.lesson_time_end,'%H:%i') > str_to_date(#{currentTime},'%H:%i')
and ((l.lesson_date = #{lessonDate} and str_to_date(l.lesson_time_start,'%H:%i') > str_to_date(#{currentTime},'%H:%i')) or l.lesson_date > #{lessonDate})
and ((#{lessonDate} = l.lesson_date and str_to_date(#{currentTime},'%H:%i') > str_to_date(l.lesson_time_end,'%H:%i')) or #{lessonDate} > l.lesson_date)
and str_to_date(#{currentTime},'%H:%i') > str_to_date(l.lesson_time_start,'%H:%i') and str_to_date(l.lesson_time_end,'%H:%i') > str_to_date(#{currentTime},'%H:%i')
and ((str_to_date(l.lesson_time_start,'%H:%i') > str_to_date(#{currentTime},'%H:%i')) or l.lesson_date > #{lessonDate})
and ((str_to_date(#{currentTime},'%H:%i') > str_to_date(l.lesson_time_end,'%H:%i')) or #{lessonDate} > l.lesson_date)
and l.lesson_date = #{lessonDate} and str_to_date(l.lesson_time_start,'%H:%i') > str_to_date(#{currentTime},'%H:%i')
and l.lesson_date > DATE_FORMAT(NOW(), '%Y-%m-%d') and l.lesson_date <= #{lessonDate}
and l.del_flag = 0 and m.del_flag = 0 and m.teach_method = 'TEACH_METHOD_LIVE' and m.publish_status = 'MODULE_PUBLISHED' and m.del_flag = 0 and m.teach_method = 'TEACH_METHOD_LIVE' and m.publish_status = 'MODULE_PUBLISHED' and n.del_flag = 0 group by l.id )AS result

