mapper.update:
update class_module_no
name = #{name}, item_one_id = #{itemOneId}, item_second_id = #{itemSecondId}, module_id = #{moduleId}, exam_term = #{examTerm}, start_date = #{startDate}, lesson_type = #{lessonType}, lesson_day = #{lessonDay}, lesson_scope = #{lessonScope}, start_time = #{startTime}, end_time = #{endTime}, lesson_hour = #{lessonHour}, teachers = #{teachers}, masters = #{masters}, assistants = #{assistants}, campus_id = #{campusId}, classroom = #{classroom}, live_room = #{liveRoom}, device = #{device}, plan_enroll_students = #{planEnrollStudents}, enroll_yet_students = #{enrollYetStudents}, publish_status = #{publishStatus}, total_hours = #{totalHours}, class_teach_type = #{classTeachType}, description = #{description}, create_time = #{createTime}, creator = #{creator}, update_time = #{updateTime}, updator = #{updator}, company_id = #{companyId}, del_flag = #{delFlag}, lesson_total = #{lessonTotal}, lesson_plan = #{lessonPlan},
id = #{id}

mapper.select:
select 
from class_module_no
where id = #{id}

sql.trim:
and name like '%${name}%'
and item_one_id = #{itemOneId}
and item_second_id = #{itemSecondId}
and module_id = #{moduleId}
and exam_term = #{examTerm}
and publish_status = #{publishStatus}
and class_teach_type = #{classTeachType}
and del_flag = 0

mapper.select:
select 
from class_module_no 

limit #{firstIndex},#{pageSize}

select distinct n.id,n.name,n.item_one_id as itemOneId,n.item_second_id as itemSecondId,n.module_id as moduleId,n.exam_term as examTerm,n.campus_id as campusId,t.term_name as examTermName ,c.campus_name as campusName 
from class_module_no n left join sys_config_campus c on n.campus_id =c.id left join sys_config_term t on n.exam_term=t.id left join class_module_no_onsale mno on mno.module_no_id=n.id 
where n.module_id=#{moduleId} and n.publish_status='MODULE_NO_ON_SALE' 
and n.campus_id=#{campusId}
and n.exam_term=#{examTerm}
and mno.classtype_id=#{classTypeId}
and mno.module_id=#{moduleId}

select name 
from class_module_no 
where campus_id in

	#{item}


	and item_one_id in
	
		#{item}
	


	and item_second_id in
	
		#{item}

select n.name 
from class_module_no n
inner join class_module m 
	on m.id = n.module_id
where m.school_id = #{companyIds}

	and n.item_one_id in
	
		#{item}
	


	and n.item_second_id in
	
		#{item}

select no.id, no.name, no.class_teach_type, one.item_name one_name, one.item_pic, two.item_name two_name, c.campus_name, no.plan_enroll_students, no.publish_status, m.name module_name, no.total_hours, no.lesson_total, no.lesson_plan from class_module_no no inner join sys_config_item one on one.id = no.item_one_id inner join sys_config_item two on two.parent_id = no.item_one_id and two.id = no.item_second_id left join sys_config_campus c on c.id = no.campus_id left join class_module m on m.id = no.module_id where m.company_id = #{companyId} and m.school_id = #{schoolId}
and no.name like concat('%',#{selNo},'%') and no.publish_status in ('MODULE_NO_CREATED','MODULE_NO_ON_SALE','MODULE_NO_OFFLINE','MODULE_NO_FINISH') order by no.publish_status,
and no.item_one_id = #{oneItemId} and no.item_second_id = #{twoItemId} and no.exam_term = #{termId} and no.class_teach_type = #{type} and no.campus_id = #{campusId} and no.publish_status = #{status} order by
no.id desc limit #{firstIndex},#{pageSize}

select count(no.id)
from class_module_no no
	inner join sys_config_item one on one.id = no.item_one_id
	inner join sys_config_item two on two.parent_id = no.item_one_id
									 and two.id = no.item_second_id
	left join sys_config_campus c on c.id = no.campus_id
	left join class_module m on m.id = no.module_id 
where m.company_id = #{companyId} and 
	m.school_id = #{schoolId}
	
		
			and no.name like concat('%',#{selNo},'%') and 
			no.publish_status in ('MODULE_NO_CREATED','MODULE_NO_ON_SALE','MODULE_NO_OFFLINE','MODULE_NO_FINISH')
		
		
			and no.item_one_id = #{oneItemId} 
			and no.item_second_id = #{twoItemId} 
			and no.exam_term = #{termId} 
			and no.class_teach_type = #{type} 
			and no.campus_id = #{campusId} 
			and no.publish_status = #{status}
