mapper.delete:
delete from class_package
where id in

	#{item}

mapper.update:
update class_package
name = #{name}, original_price = #{originalPrice}, real_price = #{realPrice}, base_num = #{baseNum}, description = #{description}, category_code = #{categoryCode}, publish_status = #{publishStatus}, publish_time = #{publishTime}, company_id = #{companyId}, school_id = #{schoolId}, creator = #{creator}, create_time = #{createTime}, update_time = #{updateTime}, updator = #{updator}, del_flag = #{delFlag}, cover = #{cover}, recommend_flag = #{recommendFlag}, introduce = #{introduce}, protocol_id = #{protocolId},
id = #{id}

mapper.select:
  select 
	from class_package
	where company_id=#{companyId} and del_flag=0
	and name = #{name}
	and school_id=#{schoolId}
	and category_code like '${categoryCode}%'
	and publish_status = #{publishStatus}

select b.*,(b.pay_count+b.base_num) as total_count from (
	SELECT DISTINCT b.com_id,IFNULL(c.buy_num ,0) as pay_count,a.*,
	IFNULL((SELECT COUNT(*) from class_package_stage b where b.class_package_id = a.id and b.del_flag = 0),0) as stage_count,
	IFNULL((SELECT count(c.id) FROM class_package_relation c,class_type d where c.class_package_id = a.id and c.class_type_id = d.id and d.del_flag = 0  ),0) as class_type_count
	FROM class_package a,commodity_product_realtion b,commodity c
	where a.id = b.product_id AND c.id = b.com_id  and a.del_flag = 0 and a.publish_status = 'CLASS_ON_SALE' and b.product_type = '2'
	and a.company_id = #{companyId}
	and a.school_id = #{schoolId}
	and a.name like '%${name}%'
	and a.category_code like '${categoryCode}%'
) b
order by ${orderBy} DESC,b.recommend_flag DESC 
limit #{firstIndex},#{pageSize};
