mapper.select:
select 
from class_type

limit #{firstIndex},#{pageSize}

		SELECT a.*, c.buy_num as actualNum 
        FROM class_type a left join commodity_product_realtion pr on
		pr.product_id = a.id
		INNER JOIN commodity c ON c.id = pr.com_id 
		where a.del_flag=0 and a.company_id = #{companyId} and c.type='COMMODITY_CLASS' 
		and a.name like '%${name}%'
		and a.item_one_id = #{itemOneId}
		and a.item_second_id = #{itemSecondId}
		and a.publish_status = #{publishStatus}
		and a.create_school_id = #{createSchoolId}
		and find_in_set(#{itemTag},a.item_tag)>0
		and find_in_set(#{itemTag2},a.item_tag)>0
		and a.teacher_id =${teacherId} 
		or a.teacher_id in(${teachersId}) 
		and a.face_flag = ${faceFlag}
		and a.live_flag = ${liveFlag}
		and a.video_flag = ${videoFlag}
		and a.remote_flag = ${remoteFlag}
		ORDER BY a.subject_class_order is null,a.subject_class_order asc, a.update_time DESC
		limit #{firstIndex},#{pageSize}

