mapper.delete:
delete from course_potocol_user_relation
where id in

	#{item}

mapper.update:
update course_potocol_user_relation
potocol_id = #{potocolId}, user_id = #{userId}, course_id = #{courseId}, record_time = #{recordTime}, company_id = #{companyId}, class_package_id = #{classPackageId},
id = #{id}

mapper.select:
select distinct(c.id) cid,c.name cname,c.updator update_userId,u.username update_userName,c.update_time ctime, (select count(id) from course_potocol_user_relation cpur where cpur.potocol_id = #{id} and cpur.course_id = cid) count from class_type c inner join course_protocol_config cpc on c.protocol_id = cpc.id left join course_potocol_user_relation cp on cpc.id = cp.potocol_id inner join users u on c.creator = u.id where cpc.id = #{id} and cpc.type = 0 and cpc.company_id = #{companyId} order by cp.id desc
select distinct(pk.id) pkid,pk.name pkname,pk.updator update_userId,u.username update_userName,pk.update_time ctime, (select count(id) from course_potocol_user_relation cpur where cpur.potocol_id = #{id} and cpur.class_package_id = pkid) count from class_package pk inner join course_protocol_config cpc on pk.protocol_id = cpc.id left join course_potocol_user_relation cp on cpc.id = cp.potocol_id inner join users u on pk.creator = u.id where cpc.id = #{id} and cpc.type = 1 and cpc.company_id = #{companyId} order by cp.id desc
limit #{firstIndex},#{pageSize}

select cpbh.course_id cid,c.name cname,c.updator update_userId,u.username update_userName,cpbh.bind_date ctime, (select count(id) from course_potocol_user_relation cpur where cpur.potocol_id = #{id} and cpur.course_id = cid) count from course_potocol_bind_history cpbh inner join course_protocol_config cpc on cpbh.potocol_id = cpc.id inner join class_type c on cpbh.course_id = c.id inner join users u on c.creator = u.id where cpc.id = #{id} and cpc.type = 0 and cpc.company_id = #{companyId}
select cpbh.class_package_id pkid,pk.name pkname,pk.updator update_userId,u.username update_userName,cpbh.bind_date ctime, (select count(id) from course_potocol_user_relation cpur where cpur.potocol_id = #{id} and cpur.class_package_id = pkid) count from course_potocol_bind_history cpbh inner join course_protocol_config cpc on cpbh.potocol_id = cpc.id inner join class_package pk on cpbh.class_package_id = pk.id inner join users u on pk.creator = u.id where cpc.id = #{id} and cpc.type = 1 and cpc.company_id = #{companyId}
limit #{firstIndex},#{pageSize}
