mapper.delete:
delete from video
where id in

	#{item}

mapper.update:
update video
video_name = #{videoName}, video_cc_id = #{videoCcId}, video_time = #{videoTime}, vodeo_size = #{vodeoSize}, video_status = #{videoStatus}, item_one_id = #{itemOneId}, item_second_id = #{itemSecondId}, video_tag = #{videoTag}, video_pic = #{videoPic}, creator = #{creator}, create_time = #{createTime}, updator = #{updator}, update_time = #{updateTime}, company_id = #{companyId}, school_id = #{schoolId}, storage_type = #{storageType}, web_video_id = #{webVideoId}, web_video_domain = #{webVideoDomain}, file_path = #{filePath},
id = #{id}

mapper.select:
select 
from video as v left join sys_config_school as scs on v.school_id = scs.id
inner join sys_config_item as sci on v.item_one_id = sci.id
where 1=1
	and video_name LIKE '%${videoName}%'
	and video_status = #{videoStatus}
	and item_one_id = #{itemOneId}
	and item_second_id = #{itemSecondId}
	and video_tag LIKE '%${videoTag}%'
	and v.company_id = #{companyId}
	
	 
	and v.create_time >= #{beginTime}
	
	
	 
	and v.create_time <= #{endTime}
	
	
	
		
			
				and (v.storage_type = 'VIDEO_STORAGE_TYPE_CC' 
					or v.storage_type = 'VIDEO_STORAGE_TYPE_QNVD' 
					or v.storage_type = 'VIDEO_STORAGE_TYPE_BLVS' 
					or v.storage_type = 'VIDEO_STORAGE_TYPE_LETV' 
					or v.storage_type = 'VIDEO_STORAGE_TYPE_SCORM'
					or v.storage_type is NULL)
			
			
			   and (v.storage_type != 'VIDEO_STORAGE_TYPE_CC' 
			   		and v.storage_type != 'VIDEO_STORAGE_TYPE_QNVD'  
					and v.storage_type != 'VIDEO_STORAGE_TYPE_BLVS'
			   		and v.storage_type != 'VIDEO_STORAGE_TYPE_LETV'
			   		and v.storage_type != 'VIDEO_STORAGE_TYPE_SCORM')
			
	   
	
order by create_time ${sortBy}
limit #{firstIndex},#{pageSize}

select count(v.id) from video as v left join sys_config_school as scs on v.school_id = scs.id inner join sys_config_item as sci on v.item_one_id = sci.id where 1=1 and video_name LIKE '%${videoName}%' and video_status = #{videoStatus} and item_one_id = #{itemOneId} and item_second_id = #{itemSecondId} and video_tag LIKE '%${videoTag}%' and v.company_id = #{companyId}
and v.create_time >= #{beginTime}
and v.create_time <= #{endTime}
and (v.storage_type = 'VIDEO_STORAGE_TYPE_CC' or v.storage_type = 'VIDEO_STORAGE_TYPE_QNVD' or v.storage_type = 'VIDEO_STORAGE_TYPE_BLVS' or v.storage_type = 'VIDEO_STORAGE_TYPE_LETV' or v.storage_type = 'VIDEO_STORAGE_TYPE_SCORM' or v.storage_type is NULL)
and (v.storage_type != 'VIDEO_STORAGE_TYPE_CC' and v.storage_type != 'VIDEO_STORAGE_TYPE_QNVD' and v.storage_type != 'VIDEO_STORAGE_TYPE_BLVS' and v.storage_type != 'VIDEO_STORAGE_TYPE_LETV' and v.storage_type != 'VIDEO_STORAGE_TYPE_SCORM')

select ifnull(sum(vodeo_size),0) from video where company_id = #{companyId} and video_status = 'VIDEO_PROCESS_NOMAL'
and create_time <= CONCAT (date_format(#{createTime}, "%Y-%m-%d")," 23:59:59") and create_time >= CONCAT (date_format(#{createTime}, "%Y-%m-%d")," 00:00:00")
and storage_type = #{storageType}

select web_video_id from video
where company_id = #{companyId}
	and storage_type = #{storageType}
