mapper.select:
select count(1) as studentNum from student s INNER JOIN users_front u ON u.id = s.user_id
left join sys_config_dict d on s.`edu_school` = d.`item_code`
where edu_identity = 0 AND IFNULL(u.`teacher_flag`,0) != 1

	and s.edu_area = #{eduArea}


	and d.item_code = #{eduSchool}

SELECT COUNT(1) AS studentNum FROM student s INNER JOIN users_front u ON u.id = s.user_id left join sys_config_dict d on s.`edu_school` = d.`item_code` WHERE edu_identity = 0 AND IFNULL(u.`teacher_flag`,0) != 1 AND edu_area IS NOT NULL and edu_area != '' AND edu_school IS NOT NULL and edu_school != '' AND edu_step IS NOT NULL and edu_step != '' AND edu_year IS NOT NULL and edu_year != '' AND edu_class IS NOT NULL and edu_class != ''
and s.edu_area = #{eduArea}
and d.item_code = #{eduSchool}

SELECT COUNT(s.`id`) AS stuNum FROM sys_config_dict c LEFT JOIN student s ON c.`item_code` = s.`edu_school` and s.`edu_identity` = 0 AND edu_area IS NOT NULL AND edu_area != '' AND edu_school IS NOT NULL AND edu_school != '' AND edu_step IS NOT NULL AND edu_step != '' AND edu_year IS NOT NULL AND edu_year != '' AND edu_class IS NOT NULL AND edu_class != '' LEFT JOIN users_front u ON u.id = s.`user_id` AND s.edu_identity = 0 LEFT JOIN sys_config_dict pc ON pc.`id` = c.`parent_item_id` LEFT JOIN edu_step_school_relation e ON e.`edu_school_id` = c.`id` WHERE 1=1
and pc.`item_code` = #{eduArea}
AND IFNULL(u.teacher_flag, 0)!=1
AND e.`edu_step_new_code` = #{eduStep}

SELECT COUNT(s.`id`) AS stuNum,c.`item_value` FROM sys_config_dict c LEFT JOIN student s ON c.`item_code` = s.`edu_school` and s.`edu_identity` = 0 AND edu_area IS NOT NULL AND edu_area != '' AND edu_school IS NOT NULL AND edu_school != '' AND edu_step IS NOT NULL AND edu_step != '' AND edu_year IS NOT NULL AND edu_year != '' AND edu_class IS NOT NULL AND edu_class != '' LEFT JOIN users_front u ON u.id = s.`user_id` AND s.edu_identity = 0 LEFT JOIN sys_config_dict pc ON pc.`id` = c.`parent_item_id` LEFT JOIN edu_step_school_relation e ON e.`edu_school_id` = c.`id` WHERE 1=1
and pc.`item_code` = #{eduArea}
AND IFNULL(u.teacher_flag, 0)!=1
AND e.`edu_step_new_code` = #{eduStep}
GROUP BY c.`item_code`

SELECT COUNT(s.`id`) AS stuNum,c.`item_value` FROM sys_config_dict c LEFT JOIN student s ON c.`item_code` = s.`edu_school` and s.`edu_identity` = 0 AND edu_area IS NOT NULL AND edu_area != '' AND edu_school IS NOT NULL AND edu_school != '' AND edu_step IS NOT NULL AND edu_step != '' AND edu_year IS NOT NULL AND edu_year != '' AND edu_class IS NOT NULL AND edu_class != '' LEFT JOIN users_front u ON u.id = s.`user_id` AND s.edu_identity = 0 LEFT JOIN sys_config_dict pc ON pc.`id` = c.`parent_item_id` LEFT JOIN edu_step_school_relation e ON e.`edu_school_id` = c.`id` WHERE 1=1
and c.`item_value` = #{orgCode}
AND IFNULL(u.teacher_flag, 0)!=1 GROUP BY c.`item_code`

select sum(a.times) as times from (select COUNT(DISTINCT(swf.user_id)) as times,swf.user_id,swf.lesson_date,s.edu_school,s.edu_area,s.edu_year from sys_watch_info  swf
LEFT JOIN student s on s.user_id = swf.user_id
LEFT JOIN edu_step_school_relation essr  on  essr.edu_school_code  = s.edu_school
where s.edu_identity=0
and edu_year = #{eduYear}
and edu_school = #{eduSchool}
and edu_area = #{eduArea}
and lesson_date >= #{startDate}
and lesson_date <= #{endDate}
and essr.edu_step_new_code = #{eduStep}
GROUP BY swf.liveroom_id) a

select COUNT(1) as times,swf.user_id,swf.lesson_date,s.edu_school,s.edu_area,s.edu_year from sys_watch_info  swf
LEFT JOIN student s on s.user_id = swf.user_id
LEFT JOIN edu_step_school_relation essr  on  essr.edu_school_code  = s.edu_school
where s.edu_identity=0
and edu_year = #{eduYear}
and edu_school = #{eduSchool}
and edu_area = #{eduArea}
and lesson_date >= #{startDate}
and lesson_date <= #{endDate}
and essr.edu_step_new_code = #{eduStep}

                    SELECT
                     swi. NAME AS class_name,
                    swi.lesson_name,
					swi.lesson_id,
                    s. NAME AS student_name,
                    sf.username as user_name,
					sf.id as user_id,
					s.edu_year,
                    CONCAT(
                        s.edu_year,
                        '级',
                        s.edu_class,
                        '班'
                    ) as study_class,
                    COUNT(swi.user_id) as times,
                    SUM(swi.watch_time) as watch_time,
					ds.item_value  as edu_school,
					dstep.item_value as edu_step
                FROM
                    sys_watch_info swi
                LEFT JOIN student s ON s.user_id = swi.user_id
                LEFT JOIN users_front sf ON sf.id = s.user_id
                LEFT JOIN commodity m ON m.id = swi.commodity_id and m.origin_type=0
				LEFT JOIN sys_config_dict ds on  ds.item_code  = s.edu_school
				LEFT JOIN  sys_config_dict dstep on  dstep.item_code  = s.edu_step
				LEFT JOIN edu_step_school_relation essr on  essr.edu_school_code = s.edu_school
				LEFT JOIN  sys_config_dict  dnstep on  dnstep.item_code = essr.edu_step_new_code
                WHERE
                    s.edu_identity=0
                    and s.edu_area = #{eduArea}
                    and s.edu_school = #{eduSchool}
                    and s.edu_step = #{eduStep}
                    and s.edu_year = #{eduYear}
                    and s.edu_class = #{eduClass}
                    and m.item_third_code = #{itemThirdCode}
                    and m.id = #{commodityId}
                    and swi.lesson_id = #{lessonId}
                    and swi.lesson_date >= #{startTime}
                    and swi.lesson_date <= #{endTime}
					and essr.edu_step_new_code = #{schoolType}
                    and (sf.username like ${userNameOrMobile} or  sf.mobile like ${userNameOrMobile})
               		 GROUP BY
                    swi.lesson_id,
                    swi.user_id
					ORDER  BY  ${orderBy}
                    limit #{firstIndex},#{pageSize}

select COUNT(1) from student_pay_master  spm
LEFT JOIN  student s on  s.id = spm.stu_id
LEFT JOIN users_front sf ON sf.id = s.user_id
LEFT JOIN  commodity c on c.id = spm.commodity_id and c.origin_type=0
LEFT JOIN  edu_step_school_relation essr on essr.edu_school_code = s.edu_school
LEFT JOIN  commodity_product_realtion cpr on  cpr.com_id = spm.commodity_id
LEFT JOIN  class_module_no_onsale cmno on cmno.classtype_id = cpr.product_id
LEFT JOIN  class_module_lesson  cml  on cml.module_no_id = cmno.module_no_id
where  s.edu_identity=0 and spm.origin_type=0 
and s.edu_area = #{eduArea}
and s.edu_school = #{eduSchool}
and s.edu_step = #{eduStep}
and s.edu_year = #{eduYear}
and s.edu_class = #{eduClass}
and c.item_third_code = #{itemThirdCode}
and c.id = #{commodityId}
and cml.id = #{lessonId}
and spm.apply_time >= #{startTime}
and spm.apply_time <= #{endTime}
and (sf.username like ${userNameOrMobile} or  sf.mobile like ${userNameOrMobile})

						select SUM(a.times) from (select  count(DISTINCT (swi.user_id)) as times from  sys_watch_info swi
						LEFT JOIN student s on  s.user_id = swi.user_id
						where s.edu_identity = 0 and s.delete_flag =  0   and s.edu_school=#{schoolId}
						and swi.lesson_date >= #{startDate}
						and swi.lesson_date <= #{endDate}
						GROUP BY swi.liveroom_id) a

select  IFNULL(sum(swi.watch_time),0) as watch_time from  sys_watch_info swi
LEFT JOIN student s on  s.user_id = swi.user_id
where s.edu_identity = 0 and s.delete_flag =  0  and s.edu_school=#{schoolId}
and swi.lesson_date >= #{startDate}
and swi.lesson_date <= #{endDate}

select  count(1) from  sys_watch_info swi
LEFT JOIN student s on  s.user_id = swi.user_id
where s.edu_identity = 0 and s.delete_flag =  0   and s.edu_school=#{schoolId}
and swi.lesson_date >= #{startDate}
and swi.lesson_date <= #{endDate}

SELECT
	join_time,leave_time,watch_time
from  sys_watch_info swi
where lesson_id = #{lessonId}
and user_id = #{userId}
ORDER  BY  join_time asc

SELECT
m.name,cml.lesson_name,swih.room_id, COUNT(swi.user_id) as times,swih.max_concurrent,count(DISTINCT(swi.user_id)) as user_num,
COUNT((CASE WHEN swi.device = 0  then 1 end)) pc,
COUNT((CASE WHEN swi.device > 0 then 1  end )) no_pc
FROM
	sys_watch_info_history swih
LEFT JOIN sys_watch_info swi ON swi.liveroom_id = swih.room_id
LEFT JOIN class_module_lesson cml ON cml.liveroom_id_gh = swih.room_id
LEFT JOIN commodity m ON m.id = swi.commodity_id and m.origin_type=0
LEFT JOIN student s ON  s.user_id = swi.user_id
where s.edu_identity = 0 and s.delete_flag =  0
and cml.lesson_date >= #{startDate}
and cml.lesson_date <= #{endDate}
and m.item_second_code = #{secondItemCode}
and m.item_third_code = #{itemThirdCode}
and m.id = #{comId}
and cml.id = #{lesson}
GROUP BY swi.liveroom_id,swih.room_id
ORDER BY ${orderBy}
limit #{firstPage},#{pageSize}

select count(IFNULL(a.time,0)) from	(SELECT
	IFNULL(count(DISTINCT(swih.room_id)),0) as time
	FROM
	sys_watch_info_history swih
	LEFT JOIN sys_watch_info swi ON swi.liveroom_id = swih.room_id
	LEFT JOIN class_module_lesson cml ON cml.liveroom_id_gh = swih.room_id
	LEFT JOIN commodity m ON m.id = swi.commodity_id and m.origin_type=0
	LEFT JOIN student s ON  s.user_id = swi.user_id
	where s.edu_identity = 0 and s.delete_flag =  0
	and cml.lesson_date >= #{startDate}
	and cml.lesson_date <= #{endDate}
	and m.item_second_code = #{secondItemCode}
	and m.item_third_code = #{itemThirdCode}
	and m.id = #{comId}
	and cml.id = #{lesson}
	GROUP BY swi.liveroom_id,swih.room_id) a

SELECT * from sys_watch_info_realtime
  where  room_id =#{roomId}
  ORDER  BY  watch_date desc

SELECT
m.name,cml.lesson_name,swih.room_id, COUNT(swi.user_id) as times,swih.max_concurrent,count(DISTINCT(swi.user_id)) as user_num,
COUNT((CASE WHEN swi.device = 0  then 1 end)) pc,
COUNT((CASE WHEN swi.device > 0 then 1  end )) no_pc
FROM
sys_watch_info_history swih
LEFT JOIN sys_watch_info swi ON swi.liveroom_id = swih.room_id
LEFT JOIN class_module_lesson cml ON cml.liveroom_id_gh = swih.room_id
LEFT JOIN commodity m ON m.id = swi.commodity_id and m.origin_type=0
LEFT JOIN student s ON  s.user_id = swi.user_id
where s.edu_identity = 0 and s.delete_flag =  0
and cml.lesson_date >= #{startDate}
and cml.lesson_date <= #{endDate}
and m.item_second_code = #{secondItemCode}
and m.item_third_code = #{itemThirdCode}
and m.id = #{comId}
and cml.id = #{lesson}
GROUP BY swi.liveroom_id,swih.room_id
limit #{pageSize}

select COUNT(1) from student_pay_master  spm
LEFT JOIN  student s on  s.id = spm.stu_id
LEFT JOIN users_front sf ON sf.id = s.user_id
LEFT JOIN  commodity c on c.id = spm.commodity_id and c.origin_type=0
LEFT JOIN  edu_step_school_relation essr on essr.edu_school_code = s.edu_school
LEFT JOIN  commodity_product_realtion cpr on  cpr.com_id = spm.commodity_id
LEFT JOIN  class_module_no_onsale cmno on cmno.classtype_id = cpr.product_id
LEFT JOIN  class_module_lesson  cml  on cml.module_no_id = cmno.module_no_id
where  s.edu_identity=0 and spm.origin_type=0 
	
		and s.is_base_school=1 
	
and c.live_flag=1
and s.edu_area = #{eduArea}
and s.edu_school = #{eduSchool}
and s.edu_step = #{eduStep}
and s.edu_year = #{eduYear}
and s.edu_class = #{eduClass}
and c.item_third_code = #{itemThirdCode}
and c.id = #{commodityId}
and cml.id = #{lessonId}
and spm.apply_time >= #{startTime}
and spm.apply_time <= #{endTime}
and (sf.username like ${userNameOrMobile} or  sf.mobile like ${userNameOrMobile})

                    SELECT
                     swi. NAME AS class_name,
                    swi.lesson_name,
					swi.lesson_id,
                    s. NAME AS student_name,
                    sf.username as user_name,
					sf.id as user_id,
					s.edu_year,
                    CONCAT(s.edu_class,'班') as study_class,
                    COUNT(swi.user_id) as times,
                    SUM(swi.watch_time) as watch_time,
					ds.item_value  as edu_school,
					dstep.item_value as edu_step
                FROM
                    sys_watch_info swi
                LEFT JOIN student s ON s.user_id = swi.user_id
                LEFT JOIN users_front sf ON sf.id = s.user_id
                LEFT JOIN commodity m ON m.id = swi.commodity_id and m.origin_type=0
				LEFT JOIN sys_config_dict ds on  ds.item_code  = s.edu_school
				LEFT JOIN  sys_config_dict dstep on  dstep.item_code  = s.edu_step
				LEFT JOIN edu_step_school_relation essr on  essr.edu_school_code = s.edu_school
				LEFT JOIN  sys_config_dict  dnstep on  dnstep.item_code = essr.edu_step_new_code
                WHERE
                    s.edu_identity=0 
                    
                    	and s.is_base_school=1 
                    
                    and m.live_flag=1
                    and s.edu_area = #{eduArea}
                    and s.edu_school = #{eduSchool}
                    and s.edu_step = #{eduStep}
                    and s.edu_year = #{eduYear}
                    and s.edu_class = #{eduClass}
                    and m.item_third_code = #{itemThirdCode}
                    and m.id = #{commodityId}
                    and swi.lesson_id = #{lessonId}
                    and swi.lesson_date >= #{startTime}
                    and swi.lesson_date <= #{endTime}
					and essr.edu_step_new_code = #{schoolType}
                    and (sf.username like ${userNameOrMobile} or  sf.mobile like ${userNameOrMobile})
               		 GROUP BY
                    swi.lesson_id,
                    swi.user_id
					ORDER  BY  ${orderBy}
                    limit #{firstIndex},#{pageSize}

