mapper.select:
		select
		pl.id,
		v.video_cc_id as video_id,
		t.name as course_name,
		d.item_value as area_name,
		d1.item_value as school_name,
		it1.item_name AS step_name,
		it.item_name as subject_name,
		s.name,
		uf.username,
		s.edu_year as year_name,
		s.edu_class as class_name,
		COUNT(pl.class_type_id) AS view_num,
        concat(SEC_TO_TIME(SUM(pl.study_length)),'') AS totle_study_length,
		round(SUM(pl.study_length) / (COUNT(pl.class_type_id) * TIME_TO_SEC(v.video_time)) * 100,2) as study_rate
		from
		sys_play_logs pl
		left JOIN class_type t on t.id = pl.class_type_id
		left join sys_config_teacher ut on ut.id = t.teacher_id
		left join student s on s.user_id = pl.id and s.edu_identity = 0
		inner join users_front uf on uf.id = s.user_id
		left join edu_step_school_relation r on r.edu_school_code = s.edu_school
		left join sys_config_dict d on d.item_code = s.edu_area
		LEFT JOIN sys_config_dict d1 on d1.item_code = s.edu_school
		LEFT JOIN sys_config_item it on it.item_code = t.item_third_code
		LEFT JOIN sys_config_item it1 on it1.item_code = t.item_second_code
		left join course_video_lecture cl on cl.id = pl.`lecture_id`
		left join video v on v.id = cl.video_id
		where s.`edu_area` IS NOT NULL
		AND s.`edu_school` IS NOT NULL
        and s.delete_flag = 0
		and s.edu_area = #{eduArea}
		and s.edu_school = #{eduSchool}
		and r.edu_step_new_code = #{eduSchoolStep}
		and s.edu_year = #{eduYear}
		and s.edu_step = #{eduStep}
		and study_time between #{startTime} and #{endTime}
		and s.edu_class like #{eduClass}
		and t.company_id = #{companyId}
		and t.id = #{classTypeId}
		and t.name = #{className} 
		and t.item_second_code = #{classStep}
		and t.item_third_code = #{classSubject}
		and (uf.username like '%${username}%' or uf.mobile = '%${username}%')
		group by pl.user_id

			order by ${fieldName} 
			order by t.name


			 ${sortType} 

		limit #{firstIndex},#{pageSize}

select pl.id, pl.class_type_id as class_type_id, v.video_cc_id as video_id, ut.name as tea_name, t.name as course_name, d.item_value as area_name, d1.item_value as school_name, it.item_name as subject_name, it1.item_name AS step_name, COUNT(pl.user_id) AS totle_study, concat(SEC_TO_TIME(SUM(pl.study_length)),'') AS totle_study_length, round(SUM(pl.study_length) / (COUNT(pl.user_id) * TIME_TO_SEC(v.video_time)) * 100,2) as study_rate, dr.item_value as school_step_name from sys_play_logs pl left JOIN class_type t on t.id = pl.class_type_id left join sys_config_teacher ut on ut.id = t.teacher_id left join student s on s.user_id = pl.user_id and s.edu_identity = 0 left join edu_step_school_relation r on r.edu_school_code = s.edu_school left join sys_config_dict dr on dr.item_code = r.edu_step_new_code left join sys_config_dict d on d.item_code = s.edu_area LEFT JOIN sys_config_dict d1 on d1.item_code = s.edu_school LEFT JOIN sys_config_item it on it.item_code = t.item_third_code LEFT JOIN sys_config_item it1 on it1.item_code = t.item_second_code left join course_video_lecture cl on cl.id = pl.`lecture_id` left join video v on v.id = cl.video_id where s.`edu_area` IS NOT NULL AND s.`edu_school` IS NOT NULL and s.delete_flag = 0 and s.edu_area = #{eduArea} and s.edu_school = #{eduSchool} and r.edu_step_new_code = #{eduSchoolStep} and t.item_third_code = #{eduSubject} and study_time between #{startTime} and #{endTime} and ut.name like '%${teaName}%' and t.company_id = #{companyId} group by pl.class_type_id
order by ${fieldName} order by ut.name
${sortType}
limit #{firstIndex},#{pageSize}
