select p.exam_term_id as examTermId,p.exam_term_name as examTermName,p.commodity_id as classTypeId,p.class_type_name as classTypeName,sum(total_amount) as totalAmount 
from student_pay_master as p left join class_type as t on p.commodity_id=t.id  
where 	p.school_id=#{schoolId} and  p.delete_flag=0 and t.type_code='CLASS_TYPE_REMOTE' and p.commodity_type='COMMODITY_CLASS'
and p.item_one_id=#{itemOneId}
and p.item_second_id=#{itemSecondId}
and p.commodity_id=#{commodityId}
and p.exam_term_id=#{examTermId}
group by p.exam_term_id,p.exam_term_name,p.commodity_id,p.class_type_name
limit #{firstIndex},#{pageSize}

select count(*) from (select p.exam_term_id as examTermId,p.exam_term_name as examTermName,p.commodity_id as classTypeId,p.class_type_name as classTypeName,sum(total_amount) from student_pay_master as p where p.school_id=#{schoolId} and  p.delete_flag=0 and p.commodity_type='COMMODITY_CLASS'
