select count(*) from (select s.id as stuId ,s.name,s.mobile,s.sex,s.age,s.education_code as educationCode,p.apply_time as applyTime,p.commodity_id as classTypeId,p.class_type_name as classTypeName,p.total_amount as  totalAmount,p.updator from student_pay_master as p,student as s  where 	p.stu_id=s.id and p.school_id=#{schoolId} and  p.delete_flag=0 and p.commodity_type='COMMODITY_CLASS'
