mapper.select:
select
from sys_config_item
order by id desc limit #{firstIndex},#{pageSize}

select
i.id,
i.item_name,
i.item_type,
i.parent_id,
i.status,
i.del_flag,
i.remark,
i.create_time,
i.creator,
i.update_time,
i.updator,
i.company_id,
i.item_pic,
i.item_back_pic,
i.item_code
from sys_config_item i, sys_school_item_relation r
where i.id=r.item_id
	and i.id = #{id}
	and i.item_name = #{itemName}
	and i.item_type = #{itemType}
	and i.parent_id = #{parentId}
	and i.status = #{status}
	and i.company_id = #{companyId}
	and r.school_id = #{schoolId}
	and i.remark = #{remark}
	and i.create_time = #{createTime}
	and i.creator = #{creator}
	and i.update_time = #{updateTime}
	and i.updator = #{updator}
	and r.del_flag = #{delFlag}
	and i.status = #{status}

mapper.update:
update sys_school_item_relation 
set del_flag = #{delFlag},
	updator = #{updator},
	update_time = #{updateTime}
	
		,true_del_flag = #{trueDelFlag}
	
where id = #{id}

mapper.select:
select id, school_id, item_id, del_flag, create_time, creator, update_time, updator, true_del_flag from sys_school_item_relation where school_id = #{schoolId} and item_id = #{itemId}
and true_del_flag = #{trueDelFlag}
limit 1

select

from sys_config_item
where item_type=1 and company_id = #{companyId}

select i.id, i.item_name, i.item_type, i.parent_id, i.status, i.del_flag, i.remark, i.create_time, i.creator, i.update_time, i.updator, i.company_id, i.item_pic, i.item_code, i.parent_code, i.item_back_pic from sys_config_item i inner join sys_school_item_relation r on r.item_id = i.id and r.school_id is not null where i.company_id = #{companyId} and r.del_flag = 0 and i.del_flag = 0 and r.true_del_flag = 0
and i.item_type = #{itemType}
and i.status = '1' order by i.sort,i.id

select i.id, i.item_name, i.item_type, i.parent_id, i.`status`, i.del_flag, i.remark, i.create_time, i.creator, i.update_time, i.updator, i.company_id, i.item_pic, i.item_code, i.parent_code, r.del_flag relation_status, i.item_back_pic from sys_config_item i inner join sys_school_item_relation r on r.item_id = i.id inner join sys_config_item_relation sci on sci.item_code=i.item_code and sci.company_id =#{companyId} where r.school_id = #{schoolId} and i.company_id = #{companyId} and i.item_type = #{itemType} and i.`status` = 1 and r.true_del_flag = 0
and i.parent_id = #{parentId}
and i.parent_code = #{parentCode}
and r.del_flag = #{delFlag}
order by i.sort,i.id

select i.id, i.item_name, i.item_type, i.parent_id, i.`status`, i.del_flag, i.remark, i.create_time, i.creator, i.update_time, i.updator, i.company_id, i.item_pic, i.item_code, i.parent_code, r.del_flag relation_status, i.item_back_pic from sys_config_item i inner join sys_school_item_relation r on r.item_id = i.id where r.school_id = #{schoolId} and i.company_id = #{companyId} and i.item_type = #{itemType} and i.`status` = 1 and i.item_code='type' and r.true_del_flag = 0
and i.parent_id = #{parentId}
and i.parent_code = #{parentCode}
and r.del_flag = #{delFlag}
order by i.sort,i.id

