select

from sys_config_teacher
where teacher_type!='PERSON_TEACHER'
and name like '%${name}%'
and teacher_type = #{teacherType}
and status_code = #{statusCode}
and mobile = #{mobile}
and school_id = #{schoolId}
and user_id = #{userId}
ORDER BY update_time DESC
limit #{firstIndex},#{pageSize}

select count(id) from sys_config_teacher
where
teacher_type!='PERSON_TEACHER'
and name like '%${name}%'
and teacher_type = #{teacherType}
and status_code = #{statusCode}
and mobile = #{mobile}
and school_id = #{schoolId}
and user_id = #{userId}

SELECT
*
FROM
sys_config_teacher a
left join
sys_config_teacher_lesson b
on a.id = b.teacher_id
WHERE a.teacher_type='PERSON_TEACHER'
and b.item_one_id = #{itemOneId}
and b.item_second_id= #{itemSecondId}
and b.module_id= #{moduleId}
and a.name like '%${name}%'
and a.mobile = #{mobile}
and status_code = #{statusCode}
and id_number = #{idNumber}
and school_id = #{schoolId}
GROUP BY a.id
ORDER BY a.update_time DESC
limit #{firstIndex},#{pageSize}

select count(1) from (SELECT
a.id
FROM
sys_config_teacher a
left join
sys_config_teacher_lesson b
on a.id = b.teacher_id
WHERE a.teacher_type='PERSON_TEACHER'
and b.item_one_id = #{itemOneId}
and b.item_second_id= #{itemSecondId}
and a.name like '%${name}%'
and mobile = #{mobile}
and status_code = #{statusCode}
and school_id = #{schoolId}
GROUP BY a.id
) teacher limit 1

select

from sys_config_teacher
where del_flag = 0 and company_id =
#{companyId} and school_id = #{schoolId}

	
		and (teacher_type = 'PERSON_MANAGER' or teacher_type =
		'PERSON_ASSISTANT' or teacher_type = 'PERSON_WAITER')
	
	
		and teacher_type = #{teacherType}
	

and mobile = #{mobile}
and name LIKE '%${name}%'
and status_code = 'TEACHER_USERD' order by sort_id is null, sort_id asc, create_time desc
limit #{firstIndex},#{pageSize}

select count(id)
from sys_config_teacher
where del_flag = 0 and company_id = #{companyId}
and school_id = #{schoolId}

	
		and (teacher_type = 'PERSON_MANAGER' or teacher_type =
		'PERSON_ASSISTANT' or teacher_type = 'PERSON_WAITER')
	
	
		and teacher_type = #{teacherType}
	

and mobile = #{mobile}
and name LIKE '%${name}%'
and status_code = 'TEACHER_USERD' order by sort_id is null,sort_id asc

select distinct a.id,a.name,a.sex,a.mobile from sys_config_teacher a
LEFT JOIN sys_config_teacher_lesson b on a.id=b.teacher_id
where status_code='TEACHER_USERD' and del_flag=0
and a.name LIKE '%${teacherName}%'
and user_id = #{userId}
and school_id = #{schoolId}
and company_id = #{companyId}
and a.teacher_type = 'PERSON_TEACHER'
and a.teacher_type != 'PERSON_TEACHER'

select

from sys_config_teacher
where user_id = #{id}

select DISTINCT a.id,a.name,a.teacher_type from sys_config_teacher a
LEFT JOIN sys_config_teacher_lesson b
on a.id=b.teacher_id where a.status_code='TEACHER_USERD' and
a.teacher_type='PERSON_TEACHER' and a.del_flag=0 and
a.company_id=#{companyId}
and a.school_id = #{schoolId}
and b.item_one_id = #{itemOneId}
and b.item_second_id= #{itemSecondId}
and a.id = #{id}
order by a.id desc

