SELECT a.id, a. NAME, a.mobile, a.headpic_url, a.resume, class_type.courseNum, commodity.stuNum from sys_config_teacher a left join ( SELECT count(id) as courseNum, teacher_id FROM class_type WHERE is_sale = 1 AND del_flag = 0 group by teacher_id ) as class_type on class_type.teacher_id = a.id left join ( SELECT sum(com.base_num + com.buy_num) as stuNum, ct.teacher_id FROM commodity com INNER JOIN commodity_product_realtion pr ON com.id = pr.com_id INNER JOIN class_type ct ON ct.id = pr.product_id WHERE com. STATUS = 1 GROUP BY ct.teacher_id ) as commodity on commodity.teacher_id = a.id WHERE a.status_code = "TEACHER_USERD" AND a.teacher_type = "PERSON_TEACHER" AND a.del_flag = 0 AND a.is_distinguished = 1 and a.name like '%${name}%'
and a.school_id in(${schoolsId})
and a.school_id = #{schoolId}
ORDER BY a.create_time DESC
limit #{firstIndex},#{pageSize}
