mapper.select:
select 
from users_front
where id = #{id}

select 
from users_front u left join student s on u.id=s.user_id
where u.id = #{id}

select 
from users_front

limit #{firstIndex},#{pageSize}

select 
from users_front
where 
	email = #{email}
	and status=1 and company_id=#{companyId}
	limit 1

select count(*)
from ( 

(select id
from users_front
where company_id=#{companyId}
and mobile = #{mobile}
and member_id is not null and member_status=1 and (member_buy_length>0 || member_buy_length=-1) and (TO_DAYS(date_format(NOW(), "%Y-%m-%d"))-TO_DAYS(member_end_time) < 0 || member_end_time is null))


union


(select id
from users_front
where company_id=#{companyId}
and username = #{username}
and member_id is not null and member_status=1 and (member_buy_length>0 || member_buy_length=-1) and (TO_DAYS(date_format(NOW(), "%Y-%m-%d"))-TO_DAYS(member_end_time) < 0 || member_end_time is null)) 
) as a

select uf.id, uf.username, uf.password, uf.mobile, uf.email, uf.school_id as schoolId, uf.status, uf.company_id as companyId, uf.email_auth_sign as emailAuthSign, uf.email_auth_sendtime as emailAuthSendtime, uf.email_auth_code as emailAuthCode, uf.user_city as userCity, uf.uuid, uf.head_pic_min as headPicMin, uf.head_pic_mid as headPicMid, uf.head_pic_max as headPicMax, uf.regist_time as registTime, uf.interests, uf.vip_flag vipFlag, uf.regist_type as registType, uf.promote_source as promoteSource, uf.promote_keyword as promoteKeyword, uf.member_id as memberId, uf.member_level as memberLevel, uf.member_buy_length as memberBuyLength, uf.member_end_time as memberEndTime, uf.member_status as memberStatus, uf.recharge as recharge, st.name, st.id as stuId, s.school_name as schoolName from users_front uf left join student st on uf.id=st.user_id left join sys_config_school s on uf.school_id=s.id where uf.company_id=#{companyId} and uf.member_buy_length is not NULL and uf.mobile like '%${mobile}%' and st.name like '%${name}%' and uf.member_id = #{memberId} and username like '%${username}%'
and TO_DAYS(uf.member_end_time)-TO_DAYS(NOW())<=#{timeLen} and TO_DAYS(uf.member_end_time)-TO_DAYS(NOW())>=0 and uf.member_buy_length <> -1 and uf.member_buy_length <> 0
and uf.member_end_time >= #{startDate }
and uf.member_end_time <= #{endDate }
and TO_DAYS(NOW())-TO_DAYS(uf.member_end_time)> 0 and uf.member_buy_length <> -1 and uf.member_buy_length <> 0
and uf.member_end_time >= #{startDate }
and uf.member_end_time <= #{endDate }
and (uf.member_end_time >= #{startDate } or uf.member_buy_length = -1 or uf.member_buy_length = 0)
and uf.member_end_time <= #{endDate }
order by st.create_time desc limit #{firstIndex},#{pageSize}

select count(uf.id)
from users_front uf left join student st on uf.id=st.user_id left join sys_config_school s on uf.school_id=s.id 
where uf.company_id=#{companyId} and  uf.member_buy_length is not NULL
and uf.mobile like '%${mobile}%'
and st.name like '%${name}%'
and uf.member_id = #{memberId}
and username like '%${username}%'

	
		and TO_DAYS(uf.member_end_time)-TO_DAYS(NOW())<=#{timeLen} and TO_DAYS(uf.member_end_time)-TO_DAYS(NOW())>=0
			 and uf.member_buy_length <> -1 and uf.member_buy_length <> 0
			
				and uf.member_end_time >= #{startDate }
			
			
				and uf.member_end_time <= #{endDate } 
			
		
		and TO_DAYS(NOW())-TO_DAYS(uf.member_end_time)> 0
			 and uf.member_buy_length <> -1 and uf.member_buy_length <> 0
			
				and uf.member_end_time >= #{startDate }
			
			
				and uf.member_end_time <= #{endDate }
			
		
		
			
				and (uf.member_end_time >= #{startDate } or uf.member_buy_length = -1 or uf.member_buy_length = 0)
			
			
				and uf.member_end_time <= #{endDate } 
			
		
	


order by st.create_time desc limit 1

select uf.id, uf.username, uf.password, uf.mobile, uf.email, uf.school_id as schoolId, uf.status, uf.company_id as companyId, uf.email_auth_sign as emailAuthSign, uf.email_auth_sendtime as emailAuthSendtime, uf.email_auth_code as emailAuthCode, uf.user_city as userCity, uf.uuid, uf.head_pic_min as headPicMin, uf.head_pic_mid as headPicMid, uf.head_pic_max as headPicMax, uf.regist_time as registTime, uf.interests, uf.vip_flag vipFlag, uf.regist_type as registType, uf.promote_source as promoteSource, uf.promote_keyword as promoteKeyword, uf.member_id as memberId, uf.member_level as memberLevel, uf.member_buy_length as memberBuyLength, uf.member_end_time as memberEndTime, uf.member_status as memberStatus, uf.recharge, st.name, st.id as stuId, s.school_name as schoolName from users_front uf left join student st on uf.id=st.user_id left join sys_config_school s on uf.school_id=s.id where uf.company_id=#{companyId} and uf.member_buy_length is not NULL and uf.mobile like '%${mobile}%' and st.name like '%${name}%' and uf.member_id = #{memberId} and username like '%${username}%'
and TO_DAYS(uf.member_end_time)-TO_DAYS(NOW())<=#{timeLen} and TO_DAYS(uf.member_end_time)-TO_DAYS(NOW())>=0 and uf.member_buy_length <> -1 and uf.member_buy_length <> 0
and uf.member_end_time >= #{startDate }
and uf.member_end_time <= #{endDate }
and TO_DAYS(NOW())-TO_DAYS(uf.member_end_time)> 0 and uf.member_buy_length <> -1 and uf.member_buy_length <> 0
and uf.member_end_time >= #{startDate }
and uf.member_end_time <= #{endDate }
and (uf.member_end_time >= #{startDate } or uf.member_buy_length = -1 or uf.member_buy_length = 0)
and uf.member_end_time <= #{endDate }
order by st.create_time desc

select 
count(*)
from users_front uf 
where uf.company_id=#{companyId}
	 and uf.school_id=#{schoolId}
	 and date_format(uf.regist_time, "%Y-%m-%d") >= '${startDate}'
	 and date_format(uf.regist_time, "%Y-%m-%d") <= '${endDate}'
	 and uf.vip_flag = #{vipFlag}
	 and uf.regist_type = #{registType}
	and TO_DAYS(NOW())-TO_DAYS(uf.regist_time)<=#{timeLen}
limit 1

select  count(u.id) num,date_format(regist_time, "%Y-%m-%d") registTime
	from users_front u
	left join student s
	on s.user_id = u.id
	LEFT JOIN sys_config_dict d
	ON d.`item_code` = s.`edu_school`
	where u.company_id=#{companyId}
		 and u.school_id=#{schoolId}
		 and date_format(regist_time, "%Y-%m-%d") >= '${startDate}'
		 and date_format(regist_time, "%Y-%m-%d") <= '${endDate}'
		 and vip_flag = #{vipFlag}
		 and regist_type = #{registType}
		and TO_DAYS(NOW())-TO_DAYS(regist_time)<=#{timeLen}
		 and s.edu_area=#{eduArea}
		 and d.item_code =#{eduSchool}
		 and edu_identity = 0 AND IFNULL(u.`teacher_flag`,0) != 1
		group by date_format(regist_time, "%Y-%m-%d")

select 
uf.id,
uf.vip_flag vipFlag,
uf.regist_type registType
from users_front uf 
where uf.company_id=#{companyId}
	 and uf.school_id=#{schoolId}
	 and date_format(uf.regist_time, "%Y-%m-%d") >= '${startDate}'
	 and date_format(uf.regist_time, "%Y-%m-%d") <= '${endDate}'
	 and uf.vip_flag = #{vipFlag}
	 and uf.regist_type = #{registType}
	and TO_DAYS(NOW())-TO_DAYS(uf.regist_time)<=#{timeLen}

select u.* 
from users_front u INNER JOIN student s on s.user_id=u.id
where s.id=#{stuId}

select id,username,mobile
from users_front
where company_id=#{companyId}
and status = #{status}
 and vip_flag = #{vipFlag}
and member_id in(${memberLevel}) and member_status=1 and (member_buy_length>0 || member_buy_length=-1) and (TO_DAYS(date_format(NOW(), "%Y-%m-%d"))-TO_DAYS(member_end_time) < 0 || member_end_time is null)
and username = #{username}
and mobile = #{mobile}
