mapper.select:
select 
from users

limit #{firstIndex},#{pageSize}

select distinct u.*
from users u 
inner join users_comany_relation ucr on ucr.user_id=u.id and ucr.is_used=1
inner join sys_config_school scs on scs.company_id=ucr.company_id
left join auth_user_role ur on u.id=ur.user_id  
where  u.user_type='USER_TYPE_ORG' and ucr.company_id=#{companyId} and u.status = #{status}
and u.real_name like '%${realName}%'
and u.mobile = #{mobile}
and scs.id = #{schoolId}
and ur.role_id = #{roleId}
limit #{firstIndex},#{pageSize}

select count(distinct u.id) from users u 
inner join users_comany_relation ucr on ucr.user_id=u.id and ucr.is_used=1
inner join sys_config_school scs on scs.company_id=ucr.company_id
left join auth_user_role ur on u.id=ur.user_id 
where u.user_type='USER_TYPE_ORG' and ucr.company_id=#{companyId}
and u.real_name like  '%${realName}%'
and u.mobile = #{mobile}
and scs.id = #{schoolId}
and u.status = #{status}
and ur.role_id = #{roleId}

select count(1) from users where 1=1
 and username = #{username}
 and mobile = #{mobile}
 and identity_id = #{identityId}
 and email = #{email}

